import "./App.css";
import { YoutubeData } from "./data";
// JSX: Javascript XML
// ES6
// Babel

//Parent
function App() {
  const numbers = [1,2,3,4,5];
  const double = numbers.map(item => item*2);
  // const name = "Evondev";

  // return (
  //   <div className="App">
