import React, { useState } from "react";
import "./ToggleStyle.css";

function Toggle() {
    const [on, setOn] = useState(false); // State to track toggle status

    return (
        <div className="toggle-control">
            <div
                className={`toggle ${on ? "active" : ""}`} // Add class "active" if on=true
                onClick={() => setOn(!on)} // Toggle state on click
            >
                <div className={`spinner ${on ? "active" : ""}`}></div>
            </div>
        </div>
    );
}

export default Toggle;
