import React, { useEffect, useRef, useState } from "react";
import axios from "axios";
import lodash from "lodash"; // Import lodash để dùng debounce

const HackerNews = () => {
  const [hits, setHits] = useState([]);
  const [query, setQuery] = useState("");
  const [loading, setLoading] = useState(false);
  const [errorMessage, setErrorMessage] = useState("");
  const handleFetchData = useRef({});
  const [url, setUrl] = useState(
    `https://hn.algolia.com/api/v1/search?query=${query}`
  );

  const isMounted = useRef(true);

  // Cleanup function to handle when component is unmounted
  useEffect(() => {
    return () => {
      isMounted.current = false;
    };
  }, []);

  handleFetchData.current = async () => {
    setLoading(true);
    try {
      const response = await axios.get(url);
      setTimeout(() => {
        if (isMounted.current) {
          setHits(response.data?.hits || []);
          setLoading(false);
        }
      }, 500);
    } catch (error) {
      setLoading(false);
      setErrorMessage(`The error happened: ${error}`);
    }
  };

  // Debounced function to handle search query change
  const handleUpdateQuery = lodash.debounce((e) => {
    setQuery(e.target.value);
  }, 1000);

  // Effect to trigger API call only when query changes
  useEffect(() => {
    if (query !== "") {
      setUrl(`https://hn.algolia.com/api/v1/search?query=${query}`);
    }
  }, [query]);

  // Effect to fetch data when URL changes
  useEffect(() => {
    if (url) {
      handleFetchData.current();
    }
  }, [url]);

  return (
    <div className="bg-white mx-auto mt-5 mb-5 p-5 rounded-lg shadow-md w-2/4">
      <div className="flex mb-5 gap-x-5">
        <input
          type="text"
          className="border border-gray-200 p-5 block w-full rounded-md transition-all focus:border-blue-400"
          placeholder="Typing your keyword..."
          value={query} // Use value instead of defaultValue to control input
          onChange={handleUpdateQuery} // Use debounced input change handler
        />
        <button
          onClick={() => setUrl(`https://hn.algolia.com/api/v1/search?query=${query}`)}
          className="bg-blue-500 text-white font-semibold p-5 rounded-md flex-shrink-0"
        >
          Fetching
        </button>
      </div>
      {loading && (
        <div className="loading w-8 h-8 rounded-full border-blue-500 border-4 border-r-4 border-r-transparent animate-spin mx-auto my-10"></div>
      )}
      {!loading && errorMessage && (
        <p className="text-red-400 my-5">{errorMessage}</p>
      )}
      <div className="flex flex-wrap gap-5">
        {!loading &&
          hits.length > 0 &&
          hits.map((item, index) => {
            if (!item.title || item.title.length <= 0) return null;
            return (
              <h3 key={item.title} className="p-3 bg-gray-100 rounded-md">
                {item.title}
              </h3>
            );
          })}
      </div>
    </div>
  );
};

export default HackerNews;
