import axios from "axios";
import React, { useState, useEffect } from "react";

const HackerNews = () => {
    const [hits, setHits] = useState([]);
    const [query, setQuery] = useState("react");
    const [loading, setLoading] = useState(true);
    const [errorMessage, setErrorMessage] = useState("");

    const handleFetchData = async () => {
        setLoading(true);
        try {
            const response = await axios.get(
                `https://hn.algolia.com/api/v1/search?query=${query}`
            );
            setHits(response.data?.hits || []);
            setLoading(false);
        } catch (error) {
            console.error("Khong tim thay du lieu:", error);
            setLoading(false);
        }
    };

    useEffect(() => {
        handleFetchData(); 
    }, [query]); 

    return (
        <div>
            <input
                type="text"
                className="border border-green-500 text-black p-5 mb-5"
                value={query}
                onChange={(e) => setQuery(e.target.value)}
            />
            {loading && <div className="loading w-8 h-8 rounded-full border-blue-500 border-4 border-r-4 border-r-transparent animate-spin mx-auto my-10"></div>
            }
            {!loading && errorMessage && (
        <p className="text-red-400 my-5">{errorMessage}</p>
      )}
            {!loading &&
                hits.length > 0 &&
                hits.map((item, index) => (
                    <h3 key={item.objectID}>{item.title || "No title"}</h3>
                ))}
        </div>
    );
};

export default HackerNews;
